#
# vim and vim accessories
#

alias vi="vim -p"
alias vim="vim -p"
alias view="vim -R"
export EDITOR=vim
alias vibash="vim ~/.bashrc"
alias vialiases="vim ~/.bash_aliases; realias"; alias vialias=vialiases
# because when you use vim a lot, you expect :q to be quit
alias :q="exit"
alias :Q="exit"
alias :wq="exit"
